# Section 3: Event Log (Windows Server 2025)
$Global:Rules += @(
  @{ Id='3.1'; Title='(L1) Ensure ''Application: Control Event Log behavior when the log file reaches its maximum size'' is set to ''Disabled'''; Section='3 Event Log'; Profile='Level1'; Type='Registry'; Key='HKLM:\SOFTWARE\Policies\Microsoft\Windows\EventLog\Application'; ValueName='Retention'; Expected=0; Description='Controls event log retention behavior'; Impact='Prevents log overwriting'; Remediation='Set Application log retention to Disabled' },
  @{ Id='3.2'; Title='(L1) Ensure ''Application: Specify the maximum log file size (KB)'' is set to ''32,768 or greater'''; Section='3 Event Log'; Profile='Level1'; Type='Registry'; Key='HKLM:\SOFTWARE\Policies\Microsoft\Windows\EventLog\Application'; ValueName='MaxSize'; Expected=32768; Description='Sets Application log maximum size'; Impact='Ensures adequate log storage'; Remediation='Set Application log size to 32768 KB or greater' },
  @{ Id='3.3'; Title='(L1) Ensure ''Security: Control Event Log behavior when the log file reaches its maximum size'' is set to ''Disabled'''; Section='3 Event Log'; Profile='Level1'; Type='Registry'; Key='HKLM:\SOFTWARE\Policies\Microsoft\Windows\EventLog\Security'; ValueName='Retention'; Expected=0; Description='Controls Security log retention'; Impact='Prevents security log overwriting'; Remediation='Set Security log retention to Disabled' },
  @{ Id='3.4'; Title='(L1) Ensure ''Security: Specify the maximum log file size (KB)'' is set to ''196,608 or greater'''; Section='3 Event Log'; Profile='Level1'; Type='Registry'; Key='HKLM:\SOFTWARE\Policies\Microsoft\Windows\EventLog\Security'; ValueName='MaxSize'; Expected=196608; Description='Sets Security log maximum size'; Impact='Ensures adequate security log storage'; Remediation='Set Security log size to 196608 KB or greater' },
  @{ Id='3.5'; Title='(L1) Ensure ''System: Control Event Log behavior when the log file reaches its maximum size'' is set to ''Disabled'''; Section='3 Event Log'; Profile='Level1'; Type='Registry'; Key='HKLM:\SOFTWARE\Policies\Microsoft\Windows\EventLog\System'; ValueName='Retention'; Expected=0; Description='Controls System log retention'; Impact='Prevents system log overwriting'; Remediation='Set System log retention to Disabled' },
  @{ Id='3.6'; Title='(L1) Ensure ''System: Specify the maximum log file size (KB)'' is set to ''32,768 or greater'''; Section='3 Event Log'; Profile='Level1'; Type='Registry'; Key='HKLM:\SOFTWARE\Policies\Microsoft\Windows\EventLog\System'; ValueName='MaxSize'; Expected=32768; Description='Sets System log maximum size'; Impact='Ensures adequate system log storage'; Remediation='Set System log size to 32768 KB or greater' }
)