# Section 4: Restricted Groups (Windows Server 2025)
$Global:Rules += @(
  @{ Id='4.1'; Title='(L1) Configure ''Backup Operators'' group membership'; Section='4 Restricted Groups'; Profile='Level1'; Type='Manual'; Expected='Empty or authorized users only'; Evidence='Check Local Users and Groups'; Description='Controls Backup Operators membership'; Impact='Prevents unauthorized backup access'; Remediation='Restrict Backup Operators membership' },
  @{ Id='4.2'; Title='(L1) Configure ''Guests'' group membership'; Section='4 Restricted Groups'; Profile='Level1'; Type='Manual'; Expected='Guest account only'; Evidence='Check Local Users and Groups'; Description='Controls Guests group membership'; Impact='Prevents unauthorized guest access'; Remediation='Restrict Guests group membership' },
  @{ Id='4.3'; Title='(L1) Configure ''Power Users'' group membership'; Section='4 Restricted Groups'; Profile='Level1'; Type='Manual'; Expected='Empty'; Evidence='Check Local Users and Groups'; Description='Controls Power Users membership'; Impact='Prevents legacy privilege escalation'; Remediation='Empty Power Users group' },
  @{ Id='4.4'; Title='(L1) Configure ''Remote Desktop Users'' group membership'; Section='4 Restricted Groups'; Profile='Level1'; Type='Manual'; Expected='Authorized users only'; Evidence='Check Local Users and Groups'; Description='Controls RDP access'; Impact='Prevents unauthorized remote access'; Remediation='Restrict Remote Desktop Users membership' },
  @{ Id='4.5'; Title='(L1) Configure ''Replicator'' group membership'; Section='4 Restricted Groups'; Profile='Level1'; Type='Manual'; Expected='Empty'; Evidence='Check Local Users and Groups'; Description='Controls Replicator membership'; Impact='Prevents unauthorized replication'; Remediation='Empty Replicator group' }
)