# Section 5: System Services (Windows Server 2025)
$Global:Rules += @(
  @{ Id='5.1'; Title='(L2) Ensure ''Print Spooler (Spooler)'' is set to ''Disabled'''; Section='5 System Services'; Profile='Level2'; Type='Registry'; Key='HKLM:\SYSTEM\CurrentControlSet\Services\Spooler'; ValueName='Start'; Expected=4; Description='Disables Print Spooler service'; Impact='Prevents PrintNightmare vulnerabilities'; Remediation='Disable Print Spooler service' },
  @{ Id='5.2'; Title='(L1) Ensure ''Windows Remote Management (WS-Management)'' is set to ''Disabled'''; Section='5 System Services'; Profile='Level1'; Type='Registry'; Key='HKLM:\SYSTEM\CurrentControlSet\Services\WinRM'; ValueName='Start'; Expected=4; Description='Disables WinRM service'; Impact='Reduces remote attack surface'; Remediation='Disable WinRM service unless required' },
  @{ Id='5.3'; Title='(L1) Ensure ''Remote Desktop Services'' is set to ''Disabled'''; Section='5 System Services'; Profile='Level1'; Type='Registry'; Key='HKLM:\SYSTEM\CurrentControlSet\Services\TermService'; ValueName='Start'; Expected=4; Description='Disables RDP service'; Impact='Prevents remote desktop attacks'; Remediation='Disable RDP unless required' },
  @{ Id='5.4'; Title='(L1) Ensure ''Windows Error Reporting Service'' is set to ''Disabled'''; Section='5 System Services'; Profile='Level1'; Type='Registry'; Key='HKLM:\SYSTEM\CurrentControlSet\Services\WerSvc'; ValueName='Start'; Expected=4; Description='Disables error reporting'; Impact='Prevents data leakage'; Remediation='Disable Windows Error Reporting' },
  @{ Id='5.5'; Title='(L2) Ensure ''Windows Push Notifications System Service'' is set to ''Disabled'''; Section='5 System Services'; Profile='Level2'; Type='Registry'; Key='HKLM:\SYSTEM\CurrentControlSet\Services\WpnService'; ValueName='Start'; Expected=4; Description='Disables push notifications'; Impact='Reduces network traffic'; Remediation='Disable push notification service' }
)